package com.ruoyi.system.domain;import java.util.Date;import org.apache.commons.lang3.builder.ToStringBuilder;import org.apache.commons.lang3.builder.ToStringStyle;import com.fasterxml.jackson.annotation.JsonFormat;import com.ruoyi.common.annotation.Excel;import com.ruoyi.common.core.domain.BaseEntity;/** 审批申请对象 approval */public class Approval extends BaseEntity{    private static final long serialVersionUID = 1L;    /** 审批ID */    private Long approvalId;    /** 标题 */    @Excel(name = "标题")    private String title;    /** 申请内容 */    @Excel(name = "申请内容")    private String content;    /** 申请人ID */    @Excel(name = "申请人ID")    private Long applicantId;    /** 状态（pending:待审批 approved:已通过 rejected:已驳回） */    @Excel(name = "状态", readConverterExp = "p=ending:待审批,a=pproved:已通过,r=ejected:已驳回")    private String status;    /** 审核意见（通过/驳回时填写） */    @Excel(name = "审核意见")    private String auditOpinion;    /** 删除标志（0存在 2删除） */    private String delFlag;    /** 申请时间（创建时间） */    @JsonFormat(pattern = "yyyy-MM-dd HH:mm:ss")    private Date applyTime;    public Long getApprovalId(){        return approvalId;    }    public void setApprovalId(Long approvalId){        this.approvalId = approvalId;    }    public String getTitle(){        return title;    }    public void setTitle(String title){        this.title = title;    }    public String getContent(){        return content;    }    public void setContent(String content){        this.content = content;    }    public Long getApplicantId(){        return applicantId;    }    public void setApplicantId(Long applicantId){        this.applicantId = applicantId;    }    public String getStatus(){        return status;    }    public void setStatus(String status){        this.status = status;    }    public String getAuditOpinion(){        return auditOpinion;    }    public void setAuditOpinion(String auditOpinion){        this.auditOpinion = auditOpinion;    }    public String getDelFlag(){        return delFlag;    }    public void setDelFlag(String delFlag){        this.delFlag = delFlag;    }    public Date getApplyTime(){        return applyTime;    }    public void setApplyTime(Date applyTime){        this.applyTime = applyTime;    }    @Override    public String toString(){        return new ToStringBuilder(this, ToStringStyle.MULTI_LINE_STYLE)            .append("approvalId", getApprovalId())            .append("title", getTitle())            .append("content", getContent())            .append("applicantId", getApplicantId())            .append("status", getStatus())            .append("auditOpinion", getAuditOpinion())            .append("delFlag", getDelFlag())            .append("applyTime", getApplyTime())            .append("remark", getRemark())            .append("createBy", getCreateBy())            .append("createTime", getCreateTime())            .append("updateBy", getUpdateBy())            .append("updateTime", getUpdateTime())            .toString();    }}