package com.ruoyi.system.domain;import java.util.Date;import java.util.List;import org.apache.commons.lang3.builder.ToStringBuilder;import org.apache.commons.lang3.builder.ToStringStyle;import com.fasterxml.jackson.annotation.JsonFormat;import com.ruoyi.common.core.domain.BaseEntity;/** 审批流程模板对象 approval_flow */public class ApprovalFlow extends BaseEntity{    private static final long serialVersionUID = 1L;    /** 流程ID */    private Long flowId;    /** 流程名称 */    private String name;    /** 描述 */    private String description;    /** 创建时间 */    @JsonFormat(pattern = "yyyy-MM-dd HH:mm:ss")    private Date createTime;    /** 更新时间 */    @JsonFormat(pattern = "yyyy-MM-dd HH:mm:ss")    private Date updateTime;    /** 步骤（非持久字段，仅用于前端提交） */    private List<ApprovalStep> steps;    public Long getFlowId(){return flowId;}    public void setFlowId(Long flowId){this.flowId = flowId;}    public String getName(){return name;}    public void setName(String name){this.name = name;}    public String getDescription(){return description;}    public void setDescription(String description){this.description = description;}    public Date getCreateTime(){return createTime;}    public void setCreateTime(Date createTime){this.createTime = createTime;}    public Date getUpdateTime(){return updateTime;}    public void setUpdateTime(Date updateTime){this.updateTime = updateTime;}    public List<ApprovalStep> getSteps(){return steps;}    public void setSteps(List<ApprovalStep> steps){this.steps = steps;}    @Override    public String toString(){        return new ToStringBuilder(this, ToStringStyle.MULTI_LINE_STYLE)            .append("flowId", getFlowId())            .append("name", getName())            .append("description", getDescription())            .append("createTime", getCreateTime())            .append("updateTime", getUpdateTime())            .append("remark", getRemark())            .toString();    }}