package com.ruoyi.system.domain;import java.util.Date;import org.apache.commons.lang3.builder.ToStringBuilder;import org.apache.commons.lang3.builder.ToStringStyle;import com.fasterxml.jackson.annotation.JsonFormat;import com.ruoyi.common.annotation.Excel;import com.ruoyi.common.core.domain.BaseEntity;/** 审批记录对象 approval_record */public class ApprovalRecord extends BaseEntity{    private static final long serialVersionUID = 1L;    /** 记录ID */    private Long recordId;    /** 审批ID */    @Excel(name = "审批ID")    private Long approvalId;    /** 动作（submit/approve/reject） */    @Excel(name = "动作")    private String action;    /** 意见 */    @Excel(name = "意见")    private String opinion;    /** 操作人ID */    @Excel(name = "操作人ID")    private Long operatorId;    /** 操作时间 */    @JsonFormat(pattern = "yyyy-MM-dd HH:mm:ss")    private Date operateTime;    public Long getRecordId(){        return recordId;    }    public void setRecordId(Long recordId){        this.recordId = recordId;    }    public Long getApprovalId(){        return approvalId;    }    public void setApprovalId(Long approvalId){        this.approvalId = approvalId;    }    public String getAction(){        return action;    }    public void setAction(String action){        this.action = action;    }    public String getOpinion(){        return opinion;    }    public void setOpinion(String opinion){        this.opinion = opinion;    }    public Long getOperatorId(){        return operatorId;    }    public void setOperatorId(Long operatorId){        this.operatorId = operatorId;    }    public Date getOperateTime(){        return operateTime;    }    public void setOperateTime(Date operateTime){        this.operateTime = operateTime;    }    @Override    public String toString(){        return new ToStringBuilder(this, ToStringStyle.MULTI_LINE_STYLE)            .append("recordId", getRecordId())            .append("approvalId", getApprovalId())            .append("action", getAction())            .append("opinion", getOpinion())            .append("operatorId", getOperatorId())            .append("operateTime", getOperateTime())            .append("remark", getRemark())            .append("createBy", getCreateBy())            .append("createTime", getCreateTime())            .append("updateBy", getUpdateBy())            .append("updateTime", getUpdateTime())            .toString();    }}