package com.ruoyi.system.domain;import org.apache.commons.lang3.builder.ToStringBuilder;import org.apache.commons.lang3.builder.ToStringStyle;import com.ruoyi.common.core.domain.BaseEntity;/** 审批流程步骤对象 approval_step */public class ApprovalStep extends BaseEntity{    private static final long serialVersionUID = 1L;    /** 步骤ID */    private Long stepId;    /** 流程ID */    private Long flowId;    /** 步骤序号（从1开始） */    private Integer orderNo;    /** 步骤名称 */    private String name;    /** 所需通过人数（会签） */    private Integer requiredCount;    /** 候选审批人ID列表，逗号分隔，可为空表示任意人可审批 */    private String candidateIds;    public Long getStepId(){return stepId;}    public void setStepId(Long stepId){this.stepId = stepId;}    public Long getFlowId(){return flowId;}    public void setFlowId(Long flowId){this.flowId = flowId;}    public Integer getOrderNo(){return orderNo;}    public void setOrderNo(Integer orderNo){this.orderNo = orderNo;}    public String getName(){return name;}    public void setName(String name){this.name = name;}    public Integer getRequiredCount(){return requiredCount;}    public void setRequiredCount(Integer requiredCount){this.requiredCount = requiredCount;}    public String getCandidateIds(){return candidateIds;}    public void setCandidateIds(String candidateIds){this.candidateIds = candidateIds;}    @Override    public String toString(){        return new ToStringBuilder(this, ToStringStyle.MULTI_LINE_STYLE)            .append("stepId", getStepId())            .append("flowId", getFlowId())            .append("orderNo", getOrderNo())            .append("name", getName())            .append("requiredCount", getRequiredCount())            .append("candidateIds", getCandidateIds())            .append("remark", getRemark())            .toString();    }}